%% Back from Cluster
% 
[xxx, yyy] = meshgrid(-1000:50:1000,-1000:50:1000);
r = load('rc21x21.mat');
r = r.r;
tallr = [flipud(r) ; r(2:end,:)];
wider = [fliplr(tallr) tallr(:,2:end)];
figure;
contourf(xxx,yyy,log10(wider))
cb = colorbar;
ylabel(cb,'Log Hop Probability')
ylabel('Distance between pins')
xlabel('Distance along pins')
title('Hopping Probability')

%% How about Hyperfine?
% Lets see how the gaps are effected.
% Conclusion: it's relevant below 500 V/cm or so, by a factor of something
% like 4, but for only half the population.
e = [0 logspace(4,9,60)];
hhh = e;
for i=1:length(e)
    hh = OH_hamiltonian_HF(0,e(i),0);
    d = diff(sort(eig(hh)));
    hhh(i) = d(end-3);
end
ssp = spapi(3,e,hhh);
gap = @(efield) fnval(ssp,efield);

figure(333); hold on
plot(e*1e-5,hhh/(6.626e-28),'c*')
dense = logspace(4,9,1000);
hold on
grid on
plot(dense*1e-5,gap(dense)/(6.626e-28),'g-')
title('Field to Gap Converter')
set(gca,'XScale','log')
xlabel('E-field (kV/cm)')
ylabel('Energy (MHz)')
set(gca,'YScale','log')


